const Title = () => <h1>โปรแกรมรายรับ - รายจ่าย</h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>
const Item = ()=>{
  const a = parseInt(prompt('ค่าเดินทาง '))
  const b = parseInt(prompt('ค่าที่พัก '))
  const c = parseInt(prompt('ค่าอาหาร '))
  return (
    <li>ค่าเดินทาง<span>{a}</span></li>,
    <li>ค่าที่พัก<span>{b}</span></li>,
    <li>ค่าอาหาร<span>{c}</span></li>
  );
}
const Amount = ()=>{
  var salary = parseInt(prompt('เงินเดือน '))

  const total = salary-(a+b+c)
  return (
    <li>ยอดเงินคงเหลือ<span>{total}</span></li>
  );
}
const Transaction = () => {
  return (
    <ul>
      {/* <li>เงินเดือน<span> +20000</span></li>
      <li>ค่าเดินทาง<span> -500</span></li>
      <li>ค่ากิน<span> -500</span></li>
      <li>ค่าที่พัก<span> -500</span></li> */}
      <Item/>
      <Amount/>
    </ul>
  );
}

function App() {
  return (
    <div>
      <Title/>
      <Description/>
      <Transaction/>

    </div>

  );
}

export default App;
