import item from "./components/Item.css";

const Title = () => 
<h1 className="item">
  โปรแกรมรายรับ - รายจ่าย
</h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>
// const Item = ()=>{

//   return (

//   );
// }
var salary = parseInt(prompt('เงินเดือน '))
var a = parseInt(prompt('ค่าเดินทาง '))
var b = parseInt(prompt('ค่าที่พัก '))
var c = parseInt(prompt('ค่าอาหาร '))

const ShowExpenses = (salary,a,b,c)=> {
  var amount = salary-(a+b+c)
  return(
    <ul className="item-list">
      <li>เงินเดือน <span>{salary}</span></li>
      <li>ค่าเดินทาง <span>{a}</span></li>
      <li>ค่าที่พัก <span>{b}</span></li>
      <li>ค่าอาหาร <span>{c}</span></li>
  </ul>
  );
}

const Transaction = () => {
  return (
      <ShowExpenses/>
  );
}

function App() {
  return (
    <div>
      <Title/>
      <Description/>
      <Transaction/>     
    </div>

  );
}

export default App;
