import "./components/Item.css";
import "./App.css";

const Title = () => 
<h1 className="item">
  โปรแกรมรายรับ - รายจ่าย
</h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>

const Item = (props)=>{
  return(
    <li>{props.title}<span>{props.amount}</span></li>
  );
}

const Transaction = () => {
  const data = [
    {title:"ค่าอาหาร",amount:2000},
    {title:"ค่าเดินทาง",amount:1000},
    {title:"ค่าที่พัก",amount:5000},
    {title:"ค่ารักษาพยาบาล",amount:3000}
  ]
  return (
    <ul className="item-list">
      <Item title={data[0].title} amount={data[0].amount}/>
    </ul>
  );
}

function App() {
  return (
    <div>
      <Title/>
      <Description/>
      <Transaction/> 
    </div>          
  );
}

export default App;
