import "./components/Item.css";
import "./App.css";
import { v4 as uuidv4 } from 'uuid';
import { useState } from 'react'
import PropTypes from 'prop-types';
import FormComponent from "./components/FormComponent";

const Title = () => 
<h1 className="item">
  โปรแกรมรายรับ - รายจ่าย
</h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>

const Item = (props)=>{
  const {title,amount} = props
  const status = amount < 0 ? "จ่าย":"รับ"
  return(
    <li>{status} {title}<span>{amount}</span></li>
  );
}
Item.propTypes={
  title:PropTypes.string.isRequired,
  amount:PropTypes.number.isRequired
}

const Transaction = (props) => {
  const {items} = props
  return (
    <ul className="item-list">
      {items.map((i)=>{
        return <Item {...i} key={uuidv4()}/>
      }
      )}
    </ul>
  );
}

function App() {
  // const initData = [
  //   {id:1,title:"ค่าอาหาร",amount:5000},
  //   {id:2,title:"ค่าเดินทาง",amount:1000},
  //   {id:3,title:"ค่าที่พัก",amount:5000},
  //   {id:4,title:"ค่ารักษาพยาบาล",amount:3000}
  // ]
  const [items,setItems] = useState([])
  const onAddNewItem = (newItem)=>{
    setItems((prevItem)=>{
      return [newItem,...prevItem]
    })
  }
  return (
    <div>
      <Title/>
      <Description/>
      <FormComponent onAddItem={onAddNewItem}/>
      <Transaction items={items}/> 
    </div>          
  );
}

export default App;
