import item from "./components/Item.css";

const Title = () => 
<h1 className="item">
  โปรแกรมรายรับ - รายจ่าย
</h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>
// const Item = ()=>{

//   return (

//   );
// }
const Amount = ()=>{
  var salary = parseInt(prompt('เงินเดือน '))
  var a = parseInt(prompt('ค่าเดินทาง '))
  var b = parseInt(prompt('ค่าที่พัก '))
  var c = parseInt(prompt('ค่าอาหาร '))
  const total = salary-(a+b+c)
  return (
    <p>ยอดเงินคงเหลือ <span>{total}</span></p>
  );
}
const Transaction = () => {
  return (
      <Amount/>
  );
}

function App() {
  return (
    <div>
      <Title/>
      <Description/>
      <ul className="item-list">
        <li><Transaction/></li>
      </ul>  
    </div>

  );
}

export default App;
