import "./components/Item.css";
import "./App.css";
import { v4 as uuidv4 } from 'uuid';
import { useState } from 'react'
import PropTypes from 'prop-types';
import FormComponent from "./components/FormComponent";
import DataContext from "./data/DataContext";
import { useContext } from "react";

const Title = () =>
  <h1>
    โปรแกรมรายรับ - รายจ่าย
  </h1>
const Description = () => <p>บันทึกข้อมูลบัญชีในแต่ละวัน</p>

const Item = (props) => {
  const { title, amount } = props
  const status = amount < 0 ? "expense" : "income"
  const symbol = amount < 0 ? "-" : "+"
  const name = useContext(DataContext)
  return (
    <li className={status}>{title}<span>{symbol}{Math.abs(amount)}</span>{name}</li>
  );
}
Item.propTypes = {
  title: PropTypes.string.isRequired,
  amount: PropTypes.number.isRequired
}

const Transaction = (props) => {
  const { items } = props
  return (
    <div>
      <ul className="item-list">
        {items.map((i) => {
          return <Item {...i} key={uuidv4()} />
        }
        )}
      </ul>

    </div>

  );
}

function App() {
  // const initData = []
  const [items, setItems] = useState([])
  const onAddNewItem = (newItem) => {
    setItems((prevItem) => {
      return [newItem, ...prevItem]
    })
  }
  return (
    <DataContext.Provider value={<i className="fa fa-trash" ></i>}>
      <div>
        <Title />
        <Description />
        <FormComponent onAddItem={onAddNewItem} />
        <Transaction items={items} />
      </div>
    </DataContext.Provider>
  );
}

export default App;
